import React, {useState, useEffect } from 'react'
import axios from 'axios'

const Airlines = () => {

  // Airline一覧を取得（api/index）
  const [airlines, setAirlines] = useState([])

  useEffect(() => {
    axios.get('http://localhost:3000/api/v1/airlines')
    .then( resp => setAirlines(resp.data.data))
    .catch( resp  => { console.log(resp) })
  }, [airlines.length])

  // AirlineごとのGrid表示用コンポーネントを作成
  const grid = airlines.map( item => {
    return (
      <span key={item.id} style="padding-left: 20px">
        {item.name}
      </span>
    )
  })

  // 描画
  return (
    <>
      <div className="header">
        <h1>OpenFlights</h1>
        <h2>Honest, unbiased airline reviews.</h2>
      </div>
      <div className="grid">
        {grid}
      </div>
    </>
  )
}

export default Airlines
